mapper.select:
select a.EXAMINE_SUBJECT_ID as examineSubjectId, a.EXAMINE_SUBJECT_NAME as examineSubjectName, a.examine_name as examineName, b.FRACTIONSUM as fractionSum, a.create_user as createUserId, b.UPDATE_DATE, to_char(b.UPDATE_DATE,'yyyy'), us.truename as userName from XLGL_EXAM_MAIN_ANSWER b left join XLGL_EXAM_EXAMINE a on b.examine_id = a.id left join BASE_APP_USER us on a.create_user = us.id where 1=1 and a.ISSUE_STATUS = '1' and a.status= '0' and b.ISNOTEXAM = '1' and b.status= '0'
and b.REPLY_USER_ID = #{replyUserId}
order by b.UPDATE_DATE desc

select count(a.EXAMINE_SUBJECT_ID) from XLGL_EXAM_EXAMINE a join XLGL_EXAM_MAIN_ANSWER b on a.id=b.examine_id where b.ISNOTEXAM = '1'and a.status= '0'
and b.REPLY_USER_ID = #{replyUserId}
and b.LEVEL = #{level}

select a.EXAMINE_SUBJECT_ID as examineSubjectId, a.EXAMINE_SUBJECT_NAME as examineSubjectName from XLGL_EXAM_EXAMINE a join XLGL_EXAM_MAIN_ANSWER b on a.id=b.examine_id where b.ISNOTEXAM = '1'and a.status= '0' and a.OVER_STATUS = '1'
and b.REPLY_USER_ID = #{replyUserId}
and b.LEVEL = #{level}
group by a.EXAMINE_SUBJECT_ID,a.EXAMINE_SUBJECT_NAME;
