mapper.select:
select xx.* from (
	select x.* from XLGL_EXAM_MAIN_ANSWER x
	left join  BASE_APP_ORGAN o on x.organ_id = o.id
	where o.parent_id = 'root'
	union all
	select * from (
			select x.* from XLGL_EXAM_MAIN_ANSWER x
			left join  BASE_APP_ORGAN o on x.organ_id = o.id
			where o.parent_id != 'root'
			order by o.sort asc
		)
	)xx
	left join  BASE_APP_USER u on xx.REPLY_USER_ID = u.id
	where 1=1 and (u.sfyx is null or u.sfyx = '0')

		  and xx.EXAMINE_ID = #{examineId}


		  and xx.MAKEUP_STATUS = #{makeupStatus}


		  and xx.REPLY_USER_NAME like '%'||#{replyUserName}||'%'


		  and xx.LEVEL = #{level}


		  and xx.ORGAN_NAME like '%'||#{organName}||'%'


		 and xx.ORGAN_ID = #{organId}


		  and xx.REPLY_USER_ID = #{replyUserId}


		  and xx.ISNOTEXAM = #{isNotExam}


		  and xx.STATUS = #{status}


		  and xx.ORGAN_ID in

			#{id}



		limit #{offset}, #{limit}

select count(*) from XLGL_EXAM_MAIN_ANSWER x
left join  BASE_APP_USER u on x.REPLY_USER_ID = u.id
where 1=1 and (u.sfyx is null or u.sfyx = '0')

	and x.EXAMINE_ID = #{examineId}


	and x.ISNOTEXAM = #{isNotExam}


	  and x.STATUS = #{status}


	  and x.LEVEL = #{level}


	and x.ORGAN_ID in

		#{id}

select IFNULL(count(distinct REPLY_USER_ID),0)  from XLGL_EXAM_MAIN_ANSWER x
left join  BASE_APP_USER u on x.REPLY_USER_ID = u.id
 where 1 = 1 and (u.sfyx is null or u.sfyx = '0')

	and x.EXAMINE_ID = #{examineId}


	and x.ISNOTEXAM = #{isNotExam}


	  and x.STATUS = #{status}


	and x.ORGAN_ID in

		#{id}

select e.EXAMINE_NAME as examineName , m.* from XLGL_EXAM_MAIN_ANSWER m join XLGL_EXAM_EXAMINE e on e.id = m.EXAMINE_ID where m.STATUS = '0' and m.ISNOTEXAM = '1'
and m.EXAMINE_ID = #{examineId}
and m.MAKEUP_STATUS = #{makeupStatus}
and m.REPLY_USER_NAME like '%'||#{replyUserName}||'%'
and m.REPLY_USER_ID = #{replyUserId}
and m.ISNOTEXAM = #{isNotExam}
and e.EXAMINE_SUBJECT_NAME like '%'||#{examineSubjectName}||'%'
and e.CREATE_DATE like '%'||#{year}||'%'
order by m.update_date desc

select
		e.EXAMINE_NAME as examineName , m.*
from
	XLGL_EXAM_MAIN_ANSWER m
join
	XLGL_EXAM_EXAMINE e on e.id = m.EXAMINE_ID
where m.STATUS  = '0'  and m.ISNOTEXAM = '1'

	  and m.EXAMINE_ID = #{examineId}


	  and m.MAKEUP_STATUS = #{makeupStatus}


	  and m.REPLY_USER_NAME like '%'||#{replyUserName}||'%'


	  and m.REPLY_USER_ID = #{replyUserId}


		and m.EXAMINE_ID = #{examId}


	  and m.ISNOTEXAM = #{isNotExam}


	 	and e.EXAMINE_SUBJECT_NAME like '%'||#{examineSubjectName}||'%'


	and e.CREATE_DATE like '%'||#{year}||'%'

	order by m.update_date desc

     select   distinct examineName as examineName,EXAMINE_ID as examineId from ( select  e.EXAMINE_NAME as examineName,  m.*
      from XLGL_EXAM_MAIN_ANSWER m join XLGL_EXAM_EXAMINE e on e.id = m.EXAMINE_ID where
        m.STATUS    = '0' and m.ISNOTEXAM = '1' 

			and to_char(m.CREATE_DATE,'yyyy') = #{year}

         order by CREATE_DATE desc
        )

select count(1) as numberAll, count(case when x.isnotexam ='0' then 1 else null end) as fillUpNum, count(case when x.isnotexam ='1' and x.level = '优秀' then 1 else null end) as total, count(case when x.isnotexam ='1' and x.level = '优良' then 1 else null end) as fine, count(case when x.isnotexam ='1' and x.level = '及格' then 1 else null end) as pass, count(case when x.isnotexam ='1' then 1 else null end) as peopleNum from XLGL_EXAM_MAIN_ANSWER x left join BASE_APP_USER u on x.REPLY_USER_ID = u.id where 1=1 and (u.sfyx is null or u.sfyx = '0') and x.ORGAN_ID in ( select id from BASE_APP_ORGAN where TREE_PATH like '%'||#{organId}||'%' )
and x.EXAMINE_ID = #{examId}
and x.STATUS = #{status}
and x.ISNOTEXAM = #{isnotexam}

select EXAMINE_ID, FRACTIONSUM, REPLY_USER_ID, LEVEL from XLGL_EXAM_MAIN_ANSWER where STATUS = '0' and ISNOTEXAM = '1'
and to_char(CREATE_DATE,'yyyy') = #{year}
order by update_date desc
